function savePost(t){let e=new URL(window.location.href),s=e.href.indexOf("post/"),o=e.href.slice(s+5),a=$("#blog-title").val(),l=$("#blog-slug").val(),r=$("#blog-meta-title").val(),i=$("#blog-meta-description").val(),u=$("#blog-meta-keywords").val(),c=$("#blog-status").val(),n=$("#blog-categories").val(),d=$("#blog-tags").val(),h=$("#blog-author").val();$.ajax({url:"/post/update/"+o,type:"POST",data:{post_id:o,title:a,slug:l,metaTitle:r,metaDescription:i,metaKeywords:u,status:c,categories:n,tags:d,author:h,content:t,featuredImage:"",scheduledDate:""},success:()=>savePostCallback(o,t),error:function(t){}})}function savePostCallback(t,e){let s=$("#history-count").text().split(" "),o=parseInt(s[1])+1;$("#history-count").text(s[0]+" "+o+" "+s[2]),$.ajax({method:"GET",url:"/post/update_history/"+t,success:()=>{$.ajax({method:"GET",url:"/refresh-session",success:()=>{window.location.reload()}})},error:t=>{}})}function publishPost(){let t=$("#post-id-hidden").text();$.ajax({method:"GET",url:"/post/publish/"+t,success:()=>{$.ajax({method:"GET",url:"/refresh-session",success:()=>{$.ajax({method:"GET",url:"/post/publish/"+t}),window.location.reload()}})},error:t=>{}})}export{savePostCallback,savePost,publishPost};