<script setup>import{reactive, ref, onMounted} from 'vue' const props=defineProps({ events: Array, ward: String,}) const today=reactive({ day: '', month: '', year: '', string: ''}) const getToday=()=>{ let d=new Date() today.day=d.getDate() today.month=d.getMonth() + 1 today.year=d.getFullYear()} const day=ref(null)const month_name=ref(null) onMounted(()=>{ getToday() let month_names=['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'] month_name.value.textContent=month_names[today.month-1] day.value.textContent=today.day}) const getTodaysEvents=()=>{ let events=props.events let todaysEvents=[] events.forEach(event=>{ let start=new Date(event.time.start) let end=new Date(event.time.end) let today=new Date() if (start <=today && end >=today){ todaysEvents.push(event)}}) return todaysEvents} </script><template><div class="agenda column flex-evenly"><div class="today"><h2 ref="day"></h2><p ref="month_name"></p></div><div class="events"><div class="event" v-for="event in getTodaysEvents()"><h3>{{event.title}}</h3><p>{{event.time.start}} -{{event.time.end}}</p></div></div></div></template><style>.agenda{ height: 95%; width: calc(100% - 4rem); margin-left:2rem; background-color: var(--active-color); border-radius: 5px; padding: 1rem;} @media screen and (max-width: 1000px){ .agenda{ min-height:500px;}}</style>