<script setup>import{Moon, Sun} from 'lucide-vue-next'const props=defineProps({ darklight: String})const emit=defineEmits(['update'])function toggle(){ if (props.darklight=='dark'){ emit('update', 'light') localStorage.setItem('darklight', 'light')} else{ emit('update', 'dark') localStorage.setItem('darklight', 'dark')}}</script><template><button @click="toggle" class="lucide-button" id="dmt"><template v-if="props.darklight=='dark'"><Sun /></template><template v-else><Moon /></template></button></template><style>#dmt{ position: fixed; top: .25rem; right: .25rem; z-index: 100; transform: scale(75%)}</style>