<script setup>import Image from './Image.vue'import{Mail, Phone} from 'lucide-vue-next' const props=defineProps({ name: String, image:{ src: String, alt: String, width: String, class: String}, position: String, email: String, phone: String, classes: String, size: String,})function setClassBySizeProp(){ if (props.size==='full'){ return 'person-name'} else{ return 'person-name nm'}}</script><template><div :class="classes"><div v-if="size==='full'" class="col-3 person-image" style='background-color: var(--lighter-gray);'><Image :src="image.src" :alt="image.alt" :width="image.width" :class="image.class"/></div><div class="col-9 col-grow"><div class="person-details"><p class="person-position">{{position}}</p><h2 :class="setClassBySizeProp()">{{name}}</h2><div v-if="size!=='full'"><div class="row flex-between"><div class="row"><div class="col-2 ht col-grow"><Mail class="icon-color lucide-icon" /></div><div class="col-10 col-grow person-email"><p><a :href="'mailto:'+email">{{email}}</a></p></div></div><div class="row"><div class="col-2 ht col-grow"><Phone class="icon-color lucide-icon" /></div><div class="col-10 col-grow person-phone"><p><a href="'tel:'+phone">{{phone}}</a></p></div></div></div></div><div v-if="size==='full'" class="row wrap flex-between"><div class="col-6 "><div class="row"><div class="col-2 ht"><Mail class="icon-color lucide-icon" /></div><div class="col-10 col-grow person-email"><p><a :href="'mailto:'+email">{{email}}</a></p></div></div></div><div class="col-6"><div class="row"><div class="col-2 ht"><Phone class="icon-color lucide-icon" /></div><div class="col-10 col-grow person-phone"><p><a href="'tel:'+phone">{{phone}}</a></p></div></div></div></div></div></div></div></template><style>body.dark .icon-color{ stroke: var(--off-white)}body.light .icon-color{ stroke: var(--gray)}body.dark .person{ background-color: var(--gray)}.person{ border-radius:4px}.person-details{ padding: min(1rem, 1vw)}.person-details .lucide-icon{ margin-top:.5rem}body.light .person{ background-color: var(--lightest-gray)}.right{ border-bottom: solid 10px var(--color1)}.person-position{ margin-bottom:0px; text-transform: uppercase; font-size:80%}.person-name{ margin-top:.25rem; border-bottom: solid 2px var(--light-gray); width:fit-content; padding-right:2rem}.nm{ margin-bottom:0px}@media screen and (min-width: 1300px) and (max-width: 1600px){ .person-details{ font-size:90%;}}@media screen and (min-width: 700px) and (max-width: 1300px){ .person-details{ font-size:80%;}}@media screen and (max-width: 700px){ .person-details{ font-size:85%;} .person-email{ width:fit-content;} .person-phone{ width:fit-content; padding-right:1rem;} .ht{ display:none;}}.person-image{ min-width:120px}</style>