<script setup>import{ ref} from 'vue'const currentTool=ref('none')function tabChanged(tab){ console.log(tab) currentTool.value=tab}const Props=defineProps({ striHTML: String, sbuHTML: String, sbmrHTML: String, vtpcHTML: String, sutftmHTML: String,})</script><template><div class="row parent wrap-t"><div class="column flex-evenly col-4 fwc tools"><button class="tool" :class="currentTool==='stri' ? 'active-tool' : ''" @click="tabChanged('stri')">Schedule temple recommend interview</button><button class="tool" :class="currentTool==='sbu' ? 'active-tool' : ''" @click="tabChanged('sbu')">Schedule building use</button><button class="tool" :class="currentTool==='sbmr' ? 'active-tool' : ''" @click="tabChanged('sbmr')">Submit building maintenance request</button><button class="tool" :class="currentTool==='vtpc' ? 'active-tool' : ''" @click="tabChanged('vtpc')">View temple/missionary preparation classes</button><button class="tool" :class="currentTool==='sutftm' ? 'active-tool' : ''" @click="tabChanged('sutftm')">Sign up to feed the missionaries</button></div><div class="column flex-between col-8 fwc tool-section"><div v-if="currentTool==='stri'"><h2>Schedule temple recommend interview</h2><div v-html="striHTML"></div></div><div v-else-if="currentTool==='sbu'"><h2>Schedule building use</h2><div v-html="sbuHTML"></div></div><div v-else-if="currentTool==='sbmr'"><h2>Submit building maintenance request</h2><div v-html="sbmrHTML"></div></div><div v-else-if="currentTool==='vsrc'"><h2>View self reliance classes</h2><div v-html="vsrcHTML"></div></div><div v-else-if="currentTool==='vtpc'"><h2>View temple/missionary preparation classes</h2><div v-html="vtpcHTML"></div></div><div v-else-if="currentTool==='sutftm'"><h2>Sign up to feed the missionaries</h2><div v-html="sutftmHTML"></div></div><div v-else-if="currentTool==='vso'"><h2>View service opportunities</h2><div v-html="vsoHTML"></div></div></div></div></template><style>.tool{ font-size:125%; padding-top:1rem; padding-bottom:1rem}.tools{ align-items:flex-end!important}@media screen and (max-width: 1200px){ .wrap-t{ flex-direction: column!important; overflow-x:hidden;} .tools{ align-items: stretch!important; max-width:90%; margin-left:5%; gap: .5rem;} .tool-section{ border-top: solid 2px var(--active-color); overflow-x:hidden;}}.tool-section{ border-left: solid 2px var(--active-color)}.active-tool{ background-color: var(--active-color)}</style>