<script setup>import{ref,watch} from 'vue' import PersonCard from './PersonCard.vue'const props=defineProps({ size:{String, default: 'full'}, colorClass: String, contacts: Array, organizations: Array, ward: String, events: Array, tools: Array, worship:{ location: String, time: String, googleMapsLink: String, image:{ src: String, alt: String, width: String, class: String}}})const currentTab=ref('contacts')function tabChanged(tab){ console.log(tab) currentTab.value=tab}function setPersonClassesByContactSize(size, ward){ let s='row person fwc' if (size==='full'){ s=s + ' col-6' + ' ' + ward} else{ s=s + ' col-3' + ' ' + ward} return s}function filterContactsByOrganization(organization){ return props.contacts.filter(contact=>contact.organization===organization)}watch(currentTab, tabChanged)</script><template><div v-for="organization in organizations"><h2>{{organization}}</h2><div class="row wrap"><PersonCard v-for="contact in filterContactsByOrganization(organization)" :key="contact.id" :name="contact.name" :image="contact.image" :position="contact.position" :email="contact.email" :phone="contact.phone" :classes="setPersonClassesByContactSize(contact.size, ward)" :size="contact.size"/></div></div></template><style>.Choctaw{ border-bottom: solid 10px var(--color1)}.Midwest-City{ border-bottom: solid 10px var(--color2)}.OKC-2nd{ border-bottom: solid 10px var(--color3)}.Moore{ border-bottom: solid 10px var(--color4)}.OKC-6th-Branch{ border-bottom: solid 10px var(--color5)}.Mustang-2nd{ border-bottom: solid 10px var(--color6)}.Mustang-1st{ border-bottom: solid 10px var(--color7)}</style>