<script setup>import{ref,watch} from 'vue' import PersonCard from './PersonCard.vue'const props=defineProps({ size:{String, default: 'full'}, colorClass: String, contacts: Array, organizations: Array, ward: String, events: Array, tools: Array, worship:{ location: String, time: String, googleMapsLink: String, image:{ src: String, alt: String, width: String, class: String}}})const currentTab=ref('contacts')function tabChanged(tab){ console.log(tab) currentTab.value=tab}function setPersonClassesByContactSize(size){ if (size==='full'){ return 'row person col-6 right fwc'} else{ return 'row person col-3 right fwc'}}function filterContactsByOrganization(organization){ return props.contacts.filter(contact=>contact.organization===organization)}watch(currentTab, tabChanged)</script><template><div v-for="organization in organizations"><h2>{{organization}}</h2><div class="row wrap"><PersonCard v-for="contact in filterContactsByOrganization(organization)" :key="contact.id" :name="contact.name" :image="contact.image" :position="contact.position" :email="contact.email" :phone="contact.phone" :classes="setPersonClassesByContactSize(contact.size)" :size="contact.size"/></div></div></template>