<script setup>import Ward from '../components/Ward.vue'import axios from 'axios'import{ onBeforeMount, onMounted, onRenderTriggered, reactive} from 'vue'import{ useRoute} from 'vue-router'const route=useRoute()//create a tools object, which will be passed to the Ward component, and get the data from the API using onMountedconst tools=reactive({ data: null,})const events=reactive({ data: null,})const contacts=reactive({ data: null,})const worship=reactive({ data: null,})onBeforeMount(async ()=>{ const ward=route.params.ward const tools_response=await axios.get('http://localhost:5220/api/tools/ward/' + ward) tools.data=tools_response.data[0] const events_response=await axios.get('http://localhost:5220/api/events/ward/' + ward) events.data=events_response.data[0] const contacts_response=await axios.get('http://localhost:5220/api/persons/ward/' + ward) contacts.data=contacts_response.data[0] const worship_response=await axios.get('http://localhost:5220/api/worships/ward/' + ward).then((response)=>{ worship.data=response.data[0]}) console.log(worship.data.time)})</script><template><Ward :ward="$props.ward" :tools="tools.data" :events="events.data" :worship="worship.data" :organizations="['Bishopric', 'Elder\'s Quorum', 'Relief Society', 'Primary', 'Young Women\'s', 'Young Men\'s']" :contacts="[ {id: 1, name: '', image:{src: '', alt: '', width: '100%', class: 'person-img square'}, position: '', email: '', phone: '', size:'', organization: '', bio: ''}, ]" /></template>