<script setup>import 'balloon-css' import{ref, onBeforeMount, reactive, onMounted} from 'vue' import Contacts from './Contacts.vue' import Events from './Events.vue' import Tools from './Tools.vue' import Worship from './Worship.vue' import axios from 'axios' const current_ward=ref("") const current_tab=ref("events") const weasel_img=ref( "data:image/png;base64," ) const tools=reactive({ data: null,})const events=reactive({ data: null,})const events_by_category=reactive({ data: null,})const contacts=reactive({ data: null,})const worship=reactive({ data: null,})function splitEventsByCategory(events){ let primary=[] let ward=[] let relief_society=[] let ywmym=[] let elders_quorum=[] let cat_match={ 'primary': primary, 'ward': ward, 'relief society': relief_society, 'ywym': ywmym, 'elder\'s quorum': elders_quorum} events.forEach((event)=>{ cat_match[event.category].push(event)}) return cat_match}const ward=ref("")const organization=ref("")onMounted(async ()=>{ let user=localStorage.getItem("user") user=JSON.parse(user) ward.value=user.user.ward organization.value=user.user.organization})</script><template><div class="row flex-center" :class="ward"><div class="col-8 fwc"><div class="row flex-center"><div class="col-10"><h1 class="">Oklahoma City South Stake - Weasel<span aria-label="Ward Events and Services Electronic List" data-balloon-pos="up">*</span></h1><h2 class="small">Editing{{//uppercase first letters in ward ward.split(" ").map((word)=>{ return word.charAt(0).toUpperCase() + word.slice(1)}).join(" ")}} at a{{organization}} level</h2></div><div class="col-2"><img :src="weasel_img" class="img" alt="Weasel"></div></div><hr /><section id="weasel-body" class="row column"><section class="tabs" :class="ward" id="weasel-tabs"><div class="tab-row row flex-between"><div class="tab-row-tabs row tab-row-tabs-calendar wrap"><div class="tab-row-tab tab calendar-tab" v-if="organization==='ward'" :class="{'activeTab': current_tab=='worship'}" @click="current_tab='worship'"><div class="tab-overlay calendar-tab-overlay" :class="{active: current_tab==='worship'}"/><h3>Worship</h3></div><div class="tab-row-tab tab calendar-tab" :class="{'activeTab': current_tab=='contacts'}" @click="current_tab='contacts'"><div class="tab-overlay calendar-tab-overlay" :class="{active: current_tab==='contacts'}"/><h3>Contacts</h3></div><div class="tab-row-tab tab calendar-tab" :class="{'activeTab': current_tab=='events'}" @click="current_tab='events'"><div class="tab-overlay calendar-tab-overlay" :class="{active: current_tab==='events'}"/><h3>Events</h3></div><div class="tab-row-tab tab calendar-tab" v-if="organization==='ward'" :class="{'activeTab': current_tab=='tools'}" @click="current_tab='tools'"><div class="tab-overlay calendar-tab-overlay" :class="{active: current_tab==='tools'}"/><h3>Tools</h3></div></div></div></section><section id="weasel-body-content" class="row flex-center center col-10 fwc"><Worship v-if="current_tab=='worship'" :ward="ward" :organization="organization" /><Contacts v-if="current_tab=='contacts'" :ward="ward" :organization="organization" /><Events v-if="current_tab=='events'" :ward="ward" :organization="organization" /><Tools v-if="current_tab=='tools'" :ward="ward" :organization="organization" /></section></section><hr><div class=" row flex-between"><p class="small">If your permissions need updated, contact<a href="mailto:josephclaytonhansen@gmail.com">Joseph Hansen</a></p><p class="small">OKCSouthStake.org is not edited, reviewed, or maintained by The Church of Jesus Christ of Latter Day Saints.</p></div></div></div></template><style>h2.small{ font-size: 120%; margin-bottom: 0}p.small{ font-size:70%; margin-top:-1.25rem; text-align: left}#weasel-tabs{ margin-top:0}#weasel-body-content{ border-top: solid 2px var(--active-color); overflow-x:hidden; overflow-y:auto; height:100%; margin-top:-1rem; padding:2rem}#weasel-body{ height:70vh; align-items:start}</style>